import React, { useEffect, useState } from 'react';
import './compStyles/Products.css';
import Axios from 'axios';
import Select from 'react-select';

const Products = () => {
	const [products, showProducts] = useState([]);

	useEffect(() => {
		Axios.get('http://localhost:3001/getProducts').then((response) => {
			showProducts(response.data);
		});
	}, []);

	const getLowProd = () => {
		Axios.get('http://localhost:3001/getProductsPriceLow').then((response) => {
			showProducts(response.data);
		});
	};

	const getHighProd = () => {
		Axios.get('http://localhost:3001/getProductsPriceHigh').then((response) => {
			showProducts(response.data);
		});
	};

	const options = [
		{
			label: 'High',
		},
		{
			label: 'Low',
		},
	];

	const onChange = (e) => {
		if (e.target.value === 'High') {
			{
				getHighProd();
			}
		}
	};
	return (
		<div className='prodBod'>
			<h1>Our Products</h1>
			<h5>(Don't want to look for it? We hear you.)</h5>

			<div className='searchBar'>
				<form>
					<div>
						<input
							className='searchButton'
							type='button'
							name='search'
							value='search'
						/>
						<input type='search' name='search bar' placeholder='ex. smarT TV' />
					</div>
					{/* <div
						style={{ color: 'red', width: '200px', backgroundColor: 'yellow' }}
					>
						<Select options={options} defaultValue='order from:' />
					</div> */}
					<div className='orderButtons'>
						<label for='cars'>Choose a car:</label>

						<select name='cars' onChange={onChange}>
							<option value='High'>High</option>
							<option value='saab'>Saab</option>
							<option value='mercedes'>Mercedes</option>
							<option value='audi'>Audi</option>
						</select>
						{/* <input
							className='orderButton'
							type='button'
							name='search'
							value='sort by $ high'
							onClick={getHighProd}
						/>

						<input
							className='orderButton'
							type='button'
							name='search'
							value='sort by $ low'
							onClick={getLowProd}
						/>

						<input
							className='orderButton'
							type='button'
							name='search'
							value='sort by category'
							onClick={getHighProd}
						/> */}
					</div>
				</form>
			</div>

			<div className='productInfo'>
				{products.map((val) => {
					return (
						<div>
							{/* <h5>{val.name}</h5> */}
							<button>Add to cart</button>
							<br />
							<br />
							<img src={val.image} alt='product' />
							<br />
							<h5>{val.name}</h5>
							{/* <p className='price'>${val.price}</p> */}
							{/* <button>Add to cart</button> */}
							<p>{val.descrip}</p>
							<p className='price'>${val.price}</p>
						</div>
					);
				})}
			</div>
		</div>
	);
};

export default Products;
